const ORDINALS: Record<string, number> = {
  "الأول": 1, "الاول": 1,
  "الثاني": 2, "الثانى": 2,
  "الثالث": 3,
  "الرابع": 4,
  "الخامس": 5,
  "السادس": 6,
  "السابع": 7,
  "الثامن": 8,
  "التاسع": 9,
  "العاشر": 10
};
const extractPartNumber = (title?: string): number | null => {
  if (!title) return null;
  const m = title.match(/(\d+)/);
  if (m) return Number(m[1]);
  for (const k of Object.keys(ORDINALS)) { if (title.includes(k)) return ORDINALS[k]; }
  return null;
};
const buildPartExam = (raw: RawPart | undefined, fallbackTitle: string): PartExam => {
  const questions = raw?.questions ?? [];
  const passScore = questions.length;
  return { title: raw?.title ?? fallbackTitle, questions, passScore };
};
export const getPartExam = (courseId: number, partId: number, partTitle?: string): PartExam => {
  const key = String(courseId);
  const parts = EXAMS[key] ?? [];
  const paired = parts.map((part, idx) => ({ part, num: extractPartNumber(part.title) ?? idx + 1 }));
  paired.sort((a,b) => a.num - b.num);
  const wanted = extractPartNumber(partTitle);
  if (wanted !== null) { const found = paired.find((p) => p.num === wanted); if (found) return buildPartExam(found.part, partTitle ?? "Part " + partId); }
  const byIndex = paired[partId - 1];
  if (byIndex) return buildPartExam(byIndex.part, partTitle ?? "Part " + partId);
  return buildPartExam(undefined, partTitle ?? "Part " + partId);
};